import React, {useState} from "react";
import {Box, Button, Select, MenuItem, makeStyles} from '@material-ui/core'

const useStyles= makeStyles({
    wrapper: {
        backgroundColor: "#fff",
        display: "flex",
        boxShadow: "0px 1px 5px rgba(0,0,0,0.1)",
        borderRadius:'5px',
        "& > *":{
            flex:1,
            height:"45px",
            margin:"8px",
        }
    }
})

export default (props) =>{

    const[search,setSearch]=useState({
        type:"Online",
        location:"Large Scale Hackathon"
    });

    const handleChange=(e)=>{
        e.persist();
        setSearch((oldState)=>({
            ...oldState,
            [e.target.name]:e.target.value,
        }))
    };

console.log(search)


    const classes = useStyles();
    return (
        <Box p={2} mt={-5} mb={2} className={classes.wrapper}>
            <Select onChange={handleChange} value={search.type} name="type" disableUnderline variant="filled" defaultValue="Online">
                <MenuItem value="Online">Online</MenuItem>
                <MenuItem value="Offline">Offline</MenuItem>
                <MenuItem value="Hybrid">Hybrid</MenuItem>
            </Select>
            <Select onChange={handleChange} value={search.location} name="location" disableUnderline variant="filled" defaultValue="Large Scale">
                <MenuItem value="Remote">Big Scale Hackathon</MenuItem>
                <MenuItem value="Office">Small Scale Hackathon </MenuItem>
            </Select>
            <Button  variant="contained" color="primary" disableElevation>
            Search
          </Button>
        </Box>
    )
}
