import React from "react";
import { Box, ThemeProvider, Grid, CircularProgress } from "@material-ui/core";
import theme from "./theme/theme";
import Header from "./components/Header";
import SearchBar from "./components/SearchBar";
import List from "./components/List/List";
import NewModel from "./components/List/NewModel";
import Ddata from './dummyData';
import { firestore,app,serverTimestamp,initializeApp } from "./Firebase/config";
import { useState } from "react";
import { useEffect } from "react";

export default () => {
  const [list,setList]=useState([]);
  const [loading,setLoading]=useState(true);
  const fetchList=async()=>{
    const req= await firestore
    .collection("list")
    .orderBy("postedOn","desc")
    .get();
  const tempList= req.docs.map((list)=>({
      ...list.data(),
      id: list.id,
      postedOn: list.data().postedOn.toDate(),
    }));
    setList(tempList);
    setLoading(false);
  }

  const postList= async details=>{
    await firestore.collection('list').add({
      ...details,
      postedOn:initializeApp.firestore.FieldValue.ServerTimestamp()
    })
  }

  useEffect(()=>{
    fetchList();
  },[])

  return (
    <ThemeProvider theme={theme}>
      <Header />
      <NewModel postList={postList}/>
      <Grid container justify="center">
        <Grid item xs={10}>
          <SearchBar/>

          {
            loading ? (
              <Box display="flex" justifyContent="center">
              <CircularProgress/>
              </Box>
            ) 
            :(
            list.map((list)=>(<List key={list.id}{...list}/>))
          )}


        </Grid>
      </Grid>
    </ThemeProvider>
  );
};
